    private void buttonAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonAddActionPerformed

        System.out.println(tableType.getSelectedIndex());
        switch(tableType.getSelectedIndex()){
            case 0:
                jDialogPeripherals.setVisible(true);
                break;
            case 1:
                jDialogCaseModel.setVisible(true);
                break;
            case 2:
                jDialogProducer.setVisible(true);
                break;
                
        }
        
        
    }//GEN-LAST:event_buttonAddActionPerformed

