    private void jButtonAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAddActionPerformed
        if (jTextFieldType.getText().isEmpty() || jTextFieldDescription.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "All fields are required!", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        PeripheralsShopDBInsert.inputPeripherals(jTextFieldType.getText(), jTextFieldDescription.getText());
        jDialogPeripherals.setVisible(false);
    }//GEN-LAST:event_jButtonAddActionPerformed

    private void jButtonAdd2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAdd2ActionPerformed
        if (jTextFieldType2.getText().isEmpty() || jTextFieldProducer.getText().isEmpty()
                || jTextFieldModel.getText().isEmpty() || jTextFieldPrice.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "All fields are required!", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        PeripheralsShopDBInsert.inputCase(jTextFieldType2.getText(), jTextFieldProducer.getText(), jTextFieldModel.getText(), jTextFieldPrice.getText(),jTextFieldDescr.getText());
        jDialogCaseModel.setVisible(false);
    }//GEN-LAST:event_jButtonAdd2ActionPerformed

