    private void addButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addButtonActionPerformed
        if (this.typeTextField.getText().isEmpty() || this.brandTextField.getText().isEmpty()
                || this.modelTextField.getText().isEmpty() || this.priceTextField.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "All fields are required!", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        if (this.addButton.isEnabled()) {
            DeviceNode dn = new DeviceNode(
                    this.typeTextField.getText(), this.brandTextField.getText(), this.modelTextField.getText(),
                    this.priceTextField.getText().isEmpty() ? "-" : this.priceTextField.getText()
            );
            Catalog.addResult = dn;
            frame.addNewItem();
            this.dispose();
        }
        
    }//GEN-LAST:event_addButtonActionPerformed

    private void cancelButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cancelButtonActionPerformed
        this.dispose();
    }//GEN-LAST:event_cancelButtonActionPerformed

