    public void checkAddCancel() {

        if (!this.typeTextField.getText().isEmpty()
                && !this.brandTextField.getText().isEmpty()
                && !this.modelTextField.getText().isEmpty()) {
            this.addButton.setEnabled(true);
        }
    }
    
